import React, { Component } from 'react';

export class AddOption extends Component {
  render() {
    const { choice, weight } = this.props.op;

    return (
      <div> style={divStyle}>       
          <button style={btnStyle} onClick={this.props.handleClick.bind(this, weight, choice)} />
      </div>
    );
  }
}


const divStyle = {
    display: "flex",
    justifyContent: "space-around",
    flexDirection: 'column',
    alignItems: "center"
};

const btnStyle = {
    background: '#ff0000',
    color: '#fff',
    border: '10px',
    padding: '10px',
    borderRadius: '10%',
    cursor: 'pointer',
    float: 'right',
  };

export default AddOption;
