export class AddOption extends Component {
  render() {
    const { choice, weight } = this.props.op;

    return (
      <div style={divStyle}>
        <h1>
          <button onClick={this.props.handleClick.bind(this, weight, choice)} />
        </h1>
      </div>
    );
  }
}

